function mouseKeyTracking(){

       let startTime = 0;
       let endTime = 0;

       // mousemove + timeStam event

 window.addEventListener('mousemove' , e => {

    console.log('X : ' + e.clientX + ', Y : ' + e.clientY);

        if(startTime === 0){
            startTime = e.timeStamp;
        } else {
            endTime = e.timeStamp
            const diffTime = (endTime - startTime) / 1000 ;

            console.log(`Wait : ${diffTime} S`);
             startTime = 0;
             endTime = 0;
            
        }
        

        document.getElementById('x-value').textContent = e.clientX
        document.getElementById('y-value').textContent = e.clientY

    } );

    // click event

    window.addEventListener('click' , e =>{
        console.log(' One Click : ' + e.detail);
        document.getElementById('click-value').textContent = e.detail ;
    });

    // Double click event

    window.addEventListener("dblclick", e=> {
      console.log("double-cliqué !");
    });


    // 3 buttons (left , middle , right) mouse event

    window.addEventListener('mousedown' , e =>{
      startTime = new Date().getTime();
        if (e.buttons === 1) {
            console.log('Bouton gauche de la souris cliqué.');
          } else if (e.buttons === 2) {
            console.log('Bouton droit de la souris cliqué.');
          } else if (e.buttons === 4) {
            console.log('Bouton du milieu de la souris cliqué.');
          }
    
    });

       
document.addEventListener("mouseup", e => {
  const endTime = new Date().getTime();
  const duration = (endTime - startTime) / 1000;
  console.log(`La durée du clic de souris est de ${duration} S`);
});

    // scrolling event

    window.addEventListener('scroll', e => {
        console.log('scroll encour ! ');
      });
    window.addEventListener('scrollend', e => {
        console.log('Scrollend ! ');
      });

}
// Ajouter un écouteur d'événement keydown
window.addEventListener('keydown', (e)=> {
  startTime = new Date().getTime();
    document.getElementById('key-value').textContent = e.key ;
  });
  
  // Ajouter un écouteur d'événement keyup
  window.addEventListener('keyup', (e)=> {
     endTime = new Date().getTime();
  const duration = (endTime - startTime) / 1000;
  console.log(`La durée du clic button est de ${duration} S`);
  
  });
  
  // Ajouter un écouteur d'événement keypress
  window.addEventListener('keypress', (e)=> {
    console.log('La touche ' + e.key + ' est maintenue enfoncée');
  });


module.exports =  { init: mouseKeyTracking } ;
     
  